import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Divider,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  Button,
  Switch,
  FormControlLabel,
  Chip,
  Alert,
  Card,
  CardContent,
  CircularProgress,
  IconButton,
  Tooltip,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  Save as SaveIcon,
  Notifications as NotificationsIcon,
  Settings as SettingsIcon
} from '@mui/icons-material';
import { alertsAPI } from '../../services/api';

// Mock alert settings data
const mockAlertSettings = [
  {
    id: 1,
    sensor_type: 'temperature',
    condition: 'gt',
    threshold: 30,
    severity: 'high',
    notification_method: ['app', 'email'],
    is_active: true
  },
  {
    id: 2,
    sensor_type: 'temperature',
    condition: 'lt',
    threshold: 10,
    severity: 'medium',
    notification_method: ['app'],
    is_active: true
  },
  {
    id: 3,
    sensor_type: 'humidity',
    condition: 'gt',
    threshold: 85,
    severity: 'medium',
    notification_method: ['app'],
    is_active: true
  },
  {
    id: 4,
    sensor_type: 'humidity',
    condition: 'lt',
    threshold: 30,
    severity: 'medium',
    notification_method: ['app', 'email'],
    is_active: true
  },
  {
    id: 5,
    sensor_type: 'soil_moisture',
    condition: 'lt',
    threshold: 20,
    severity: 'high',
    notification_method: ['app', 'email', 'sms'],
    is_active: true
  }
];

// Supported sensor types
const SENSOR_TYPES = [
  { id: 'temperature', name: 'Temperature', unit: '°C' },
  { id: 'humidity', name: 'Humidity', unit: '%' },
  { id: 'light', name: 'Light', unit: 'lux' },
  { id: 'soil_moisture', name: 'Soil Moisture', unit: '%' },
  { id: 'co2', name: 'CO2', unit: 'ppm' },
  { id: 'ph', name: 'pH Value', unit: '' },
  { id: 'water_level', name: 'Water Level', unit: '%' }
];

// Condition types
const CONDITIONS = [
  { id: 'gt', name: 'Greater Than' },
  { id: 'lt', name: 'Less Than' },
  { id: 'eq', name: 'Equal To' },
  { id: 'gte', name: 'Greater Than or Equal To' },
  { id: 'lte', name: 'Less Than or Equal To' }
];

// Severity levels
const SEVERITY_LEVELS = [
  { id: 'high', name: 'Critical', color: 'error' },
  { id: 'medium', name: 'Warning', color: 'warning' },
  { id: 'low', name: 'Information', color: 'info' }
];

// Notification methods
const NOTIFICATION_METHODS = [
  { id: 'app', name: 'In-App Notification' },
  { id: 'email', name: 'Email' },
  { id: 'sms', name: 'SMS' },
  { id: 'webhook', name: 'Webhook' }
];

// User preferences
const DEFAULT_USER_PREFERENCES = {
  receive_email_notifications: true,
  receive_sms_notifications: false,
  email_address: 'user@example.com',
  phone_number: '',
  mute_notifications: false,
  auto_acknowledge_low_severity: false
};

const AlertSettings = () => {
  // State management
  const [alertSettings, setAlertSettings] = useState([]);
  const [userPreferences, setUserPreferences] = useState(DEFAULT_USER_PREFERENCES);
  const [loading, setLoading] = useState(true);
  const [saveLoading, setSaveLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [selectedSetting, setSelectedSetting] = useState(null);
  const [newSetting, setNewSetting] = useState({
    sensor_type: '',
    condition: 'gt',
    threshold: '',
    severity: 'medium',
    notification_method: ['app'],
    is_active: true
  });
  const [formMode, setFormMode] = useState('new'); // 'new' or 'edit'

  // Load alert settings
  const fetchAlertSettings = async () => {
    setLoading(true);
    try {
      // In actual project, call API
      // const response = await alertsAPI.getSettings();
      // setAlertSettings(response.data.data);
      
      // Mock API response
      setAlertSettings(mockAlertSettings);
      
      // Load user preferences
      // const prefsResponse = await alertsAPI.getUserPreferences();
      // setUserPreferences(prefsResponse.data.data);
      
      // Use default settings
      setUserPreferences(DEFAULT_USER_PREFERENCES);
    } catch (err) {
      console.error('Failed to get alert settings:', err);
      setError('Failed to load alert settings. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Initial load
  useEffect(() => {
    fetchAlertSettings();
  }, []);

  // Add new alert setting
  const handleAddSetting = async () => {
    // Validate form
    if (!newSetting.sensor_type || !newSetting.threshold || !newSetting.notification_method.length) {
      setError('Please fill in all required fields.');
      return;
    }

    setSaveLoading(true);
    try {
      // In actual project, call API
      // const response = await alertsAPI.createSetting(newSetting);
      // const createdSetting = response.data.data;
      
      // Mock create response
      const createdSetting = {
        ...newSetting,
        id: alertSettings.length + 1,
        threshold: parseFloat(newSetting.threshold)
      };
      
      // Update local state
      if (formMode === 'new') {
        setAlertSettings([...alertSettings, createdSetting]);
        // Reset form
        setNewSetting({
          sensor_type: '',
          condition: 'gt',
          threshold: '',
          severity: 'medium',
          notification_method: ['app'],
          is_active: true
        });
        setSuccess('Alert setting created successfully.');
      } else {
        // Edit mode - update existing setting
        const updatedSettings = alertSettings.map(setting => 
          setting.id === createdSetting.id ? createdSetting : setting
        );
        setAlertSettings(updatedSettings);
        setFormMode('new');
        setSelectedSetting(null);
        setSuccess('Alert setting updated successfully.');
      }
    } catch (err) {
      console.error('Failed to save alert setting:', err);
      setError('Save failed. Please try again later.');
    } finally {
      setSaveLoading(false);
    }
  };

  // Edit existing alert setting
  const handleEditSetting = (setting) => {
    setSelectedSetting(setting);
    setNewSetting({...setting});
    setFormMode('edit');
  };

  // Delete alert setting
  const handleDeleteSetting = async (id) => {
    try {
      // In actual project, call API
      // await alertsAPI.deleteSetting(id);
      
      // Update local state
      setAlertSettings(alertSettings.filter(setting => setting.id !== id));
      setSuccess('Alert setting deleted successfully.');
      
      // If currently editing the deleted setting, reset form
      if (selectedSetting && selectedSetting.id === id) {
        setSelectedSetting(null);
        setNewSetting({
          sensor_type: '',
          condition: 'gt',
          threshold: '',
          severity: 'medium',
          notification_method: ['app'],
          is_active: true
        });
        setFormMode('new');
      }
    } catch (err) {
      console.error('Failed to delete alert setting:', err);
      setError('Delete failed. Please try again later.');
    }
  };

  // Update user preferences
  const handleSaveUserPreferences = async () => {
    setSaveLoading(true);
    try {
      // In actual project, call API
      // await alertsAPI.updateUserPreferences(userPreferences);
      
      setSuccess('User preferences saved successfully.');
    } catch (err) {
      console.error('Failed to save user preferences:', err);
      setError('Save failed. Please try again later.');
    } finally {
      setSaveLoading(false);
    }
  };

  // Handle notification method change
  const handleNotificationMethodChange = (method) => {
    const currentMethods = [...newSetting.notification_method];
    if (currentMethods.includes(method)) {
      setNewSetting({
        ...newSetting,
        notification_method: currentMethods.filter(m => m !== method)
      });
    } else {
      setNewSetting({
        ...newSetting,
        notification_method: [...currentMethods, method]
      });
    }
  };

  // Render alert setting form
  const renderSettingForm = () => {
    const sensorType = SENSOR_TYPES.find(type => type.id === newSetting.sensor_type) || {};
    
    return (
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          {formMode === 'new' ? 'Add New Alert Setting' : 'Edit Alert Setting'}
        </Typography>
        <Divider sx={{ mb: 3 }} />
        
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Sensor Type</InputLabel>
              <Select
                value={newSetting.sensor_type}
                label="Sensor Type"
                onChange={(e) => setNewSetting({
                  ...newSetting,
                  sensor_type: e.target.value
                })}
              >
                {SENSOR_TYPES.map(type => (
                  <MenuItem key={type.id} value={type.id}>
                    {type.name} ({type.unit})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Condition</InputLabel>
              <Select
                value={newSetting.condition}
                label="Condition"
                onChange={(e) => setNewSetting({
                  ...newSetting,
                  condition: e.target.value
                })}
              >
                {CONDITIONS.map(condition => (
                  <MenuItem key={condition.id} value={condition.id}>
                    {condition.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Threshold"
              type="number"
              value={newSetting.threshold}
              onChange={(e) => setNewSetting({
                ...newSetting,
                threshold: e.target.value
              })}
              InputProps={{
                endAdornment: sensorType.unit
              }}
            />
          </Grid>
          
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Severity</InputLabel>
              <Select
                value={newSetting.severity}
                label="Severity"
                onChange={(e) => setNewSetting({
                  ...newSetting,
                  severity: e.target.value
                })}
              >
                {SEVERITY_LEVELS.map(level => (
                  <MenuItem key={level.id} value={level.id}>
                    {level.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          
          <Grid item xs={12}>
            <Typography variant="subtitle2" gutterBottom>
              Notification Method
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {NOTIFICATION_METHODS.map(method => (
                <Chip
                  key={method.id}
                  label={method.name}
                  onClick={() => handleNotificationMethodChange(method.id)}
                  color={newSetting.notification_method.includes(method.id) ? 'primary' : 'default'}
                  variant={newSetting.notification_method.includes(method.id) ? 'filled' : 'outlined'}
                />
              ))}
            </Box>
          </Grid>
          
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={newSetting.is_active}
                  onChange={(e) => setNewSetting({
                    ...newSetting,
                    is_active: e.target.checked
                  })}
                />
              }
              label="Enable This Alert Setting"
            />
          </Grid>
          
          <Grid item xs={12} sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
            {formMode === 'edit' && (
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => {
                  setFormMode('new');
                  setSelectedSetting(null);
                  setNewSetting({
                    sensor_type: '',
                    condition: 'gt',
                    threshold: '',
                    severity: 'medium',
                    notification_method: ['app'],
                    is_active: true
                  });
                }}
                sx={{ mr: 1 }}
              >
                Cancel
              </Button>
            )}
            <Button
              variant="contained"
              startIcon={<SaveIcon />}
              onClick={handleAddSetting}
              disabled={saveLoading}
            >
              {saveLoading ? <CircularProgress size={24} /> : (formMode === 'new' ? 'Add' : 'Save')}
            </Button>
          </Grid>
        </Grid>
      </Paper>
    );
  };

  // Render user preferences
  const renderUserPreferences = () => {
    return (
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Notification Preferences
        </Typography>
        <Divider sx={{ mb: 3 }} />
        
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={userPreferences.receive_email_notifications}
                  onChange={(e) => setUserPreferences({
                    ...userPreferences,
                    receive_email_notifications: e.target.checked
                  })}
                />
              }
              label="Receive Email Notifications"
            />
          </Grid>
          
          {userPreferences.receive_email_notifications && (
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Email Address"
                type="email"
                value={userPreferences.email_address}
                onChange={(e) => setUserPreferences({
                  ...userPreferences,
                  email_address: e.target.value
                })}
              />
            </Grid>
          )}
          
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={userPreferences.receive_sms_notifications}
                  onChange={(e) => setUserPreferences({
                    ...userPreferences,
                    receive_sms_notifications: e.target.checked
                  })}
                />
              }
              label="Receive SMS Notifications"
            />
          </Grid>
          
          {userPreferences.receive_sms_notifications && (
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Phone Number"
                value={userPreferences.phone_number}
                onChange={(e) => setUserPreferences({
                  ...userPreferences,
                  phone_number: e.target.value
                })}
              />
            </Grid>
          )}
          
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={userPreferences.mute_notifications}
                  onChange={(e) => setUserPreferences({
                    ...userPreferences,
                    mute_notifications: e.target.checked
                  })}
                />
              }
              label="Mute All Notifications"
            />
          </Grid>
          
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={userPreferences.auto_acknowledge_low_severity}
                  onChange={(e) => setUserPreferences({
                    ...userPreferences,
                    auto_acknowledge_low_severity: e.target.checked
                  })}
                />
              }
              label="Automatically Acknowledge Low Severity Alerts"
            />
          </Grid>
          
          <Grid item xs={12} sx={{ display: 'flex', justifyContent: 'flex-end', mt: 2 }}>
            <Button
              variant="contained"
              startIcon={<SaveIcon />}
              onClick={handleSaveUserPreferences}
              disabled={saveLoading}
            >
              {saveLoading ? <CircularProgress size={24} /> : 'Save Settings'}
            </Button>
          </Grid>
        </Grid>
      </Paper>
    );
  };

  // Render existing alert settings list
  const renderAlertSettingsList = () => {
    return (
      <Paper sx={{ mb: 4 }}>
        <Box sx={{ p: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="h6">Existing Alert Settings</Typography>
          <Button
            startIcon={<AddIcon />}
            color="primary"
            onClick={() => {
              setFormMode('new');
              setSelectedSetting(null);
              setNewSetting({
                sensor_type: '',
                condition: 'gt',
                threshold: '',
                severity: 'medium',
                notification_method: ['app'],
                is_active: true
              });
            }}
          >
            Add New Setting
          </Button>
        </Box>
        <Divider />
        
        <List>
          {alertSettings.map((setting) => {
            const sensorType = SENSOR_TYPES.find(type => type.id === setting.sensor_type) || {};
            const condition = CONDITIONS.find(cond => cond.id === setting.condition) || {};
            const severity = SEVERITY_LEVELS.find(sev => sev.id === setting.severity) || {};
            
            return (
              <ListItem
                key={setting.id}
                sx={{
                  borderLeft: 4,
                  borderColor: setting.is_active ? 
                    `${severity.color}.main` : 
                    'action.disabled',
                  bgcolor: (selectedSetting && selectedSetting.id === setting.id) ? 
                    'action.selected' : 
                    'inherit'
                }}
              >
                <ListItemText
                  primary={
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <Typography variant="subtitle1">
                        {sensorType.name} {condition.name} {setting.threshold} {sensorType.unit}
                      </Typography>
                      {!setting.is_active && (
                        <Chip 
                          label="Disabled" 
                          color="default" 
                          size="small" 
                          sx={{ ml: 1 }} 
                        />
                      )}
                    </Box>
                  }
                  secondary={
                    <Box>
                      <Chip 
                        label={severity.name} 
                        color={severity.color} 
                        size="small" 
                        sx={{ mr: 1, mt: 1 }} 
                      />
                      {setting.notification_method.map(method => {
                        const methodInfo = NOTIFICATION_METHODS.find(m => m.id === method) || {};
                        return (
                          <Chip 
                            key={method}
                            label={methodInfo.name} 
                            variant="outlined"
                            size="small" 
                            sx={{ mr: 1, mt: 1 }} 
                          />
                        );
                      })}
                    </Box>
                  }
                />
                <ListItemSecondaryAction>
                  <Tooltip title="Edit">
                    <IconButton 
                      edge="end" 
                      onClick={() => handleEditSetting(setting)}
                      sx={{ mr: 1 }}
                    >
                      <SettingsIcon />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Delete">
                    <IconButton 
                      edge="end" 
                      onClick={() => handleDeleteSetting(setting.id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </Tooltip>
                </ListItemSecondaryAction>
              </ListItem>
            );
          })}
          
          {alertSettings.length === 0 && (
            <ListItem>
              <ListItemText
                primary="No Alert Settings"
                secondary="Click above button to add new alert setting"
              />
            </ListItem>
          )}
        </List>
      </Paper>
    );
  };

  // Load state
  if (loading && alertSettings.length === 0) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      {/* Message prompt */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}
      
      {success && (
        <Alert severity="success" sx={{ mb: 3 }} onClose={() => setSuccess(null)}>
          {success}
        </Alert>
      )}
      
      {/* Alert setting form */}
      {renderSettingForm()}
      
      {/* Existing alert settings list */}
      {renderAlertSettingsList()}
      
      {/* User preferences */}
      {renderUserPreferences()}
    </Box>
  );
};

export default AlertSettings;
